import React, { useState } from 'react';
import ProductList from '../components/ProductList'; // For displaying product list
import '../styles/AdminPage.css'; // Ensure this path is correct
import api from '../api/axios'; // Import API utility

const AdminPage = () => {
  const [name, setName] = useState('');
  const [description, setDescription] = useState('');
  const [price, setPrice] = useState('');
  const [image, setImage] = useState('');

  const handleAddProduct = async (e) => {
    e.preventDefault();
    try {
      const newProduct = { name, description, price: parseFloat(price), image };
      await api.post('/products', newProduct); // Adjust the endpoint as necessary
      setName('');
      setDescription('');
      setPrice('');
      setImage('');
      alert('Product added successfully!');
    } catch (error) {
      console.error('Error adding product:', error);
      alert('Error adding product. Please try again.');
    }
  };

  return (
    <div className="admin-page">
      <h2>Admin Dashboard</h2>
      
      <form onSubmit={handleAddProduct} className="add-product-form">
        <h3>Add New Product</h3>
        <label>
          Name:
          <input
            type="text"
            value={name}
            onChange={(e) => setName(e.target.value)}
            required
          />
        </label>
        <label>
          Description:
          <textarea
            value={description}
            onChange={(e) => setDescription(e.target.value)}
            required
          />
        </label>
        <label>
          Price:
          <input
            type="number"
            value={price}
            onChange={(e) => setPrice(e.target.value)}
            step="0.01"
            required
          />
        </label>
        <label>
          Image URL:
          <input
            type="text"
            value={image}
            onChange={(e) => setImage(e.target.value)}
            required
          />
        </label>
        <button type="submit">Add Product</button>
      </form>

      <ProductList />
    </div>
  );
};

export default AdminPage;
