import React, { useEffect, useState, useContext } from 'react';
import { CartContext } from '../context/CartContext';
import api from '../api/axios';
import '../styles/ProductList.css';

const sampleProducts = [
  { id: 1, name: "Eco-friendly Water Bottle", description: "Reusable, BPA-free water bottle", price: 15.99, stock: 100 },
  { id: 2, name: "Wireless Earbuds", description: "High-quality sound with long battery life", price: 79.99, stock: 50 },
  { id: 3, name: "Organic Cotton T-shirt", description: "Soft, breathable, and sustainably made", price: 24.99, stock: 75 },
  { id: 4, name: "Smart Home Security Camera", description: "HD video with night vision and two-way audio", price: 129.99, stock: 30 },
  { id: 5, name: "Portable Solar Charger", description: "Charge your devices on the go with solar power", price: 49.99, stock: 60 },
];

const ProductList = () => {
  const [products, setProducts] = useState(sampleProducts);
  const { cart, setCart } = useContext(CartContext);

  useEffect(() => {
    const fetchProducts = async () => {
      try {
        const response = await api.get('/products');
        setProducts(response.data.data);
      } catch (error) {
        console.error('Error fetching products:', error);
      }
    };
    fetchProducts();
  }, []);

  const addToCart = (product) => {
    setCart([...cart, product]);
  };

  return (
    <div className="product-list">
      <h2>Products</h2>
      <ul>
        {products.map((product) => (
          <li key={product.id} className="product-item">
            <h3>{product.name}</h3>
            <p>{product.description}</p>
            <p>Price: ${product.price.toFixed(2)}</p>
            <p>In Stock: {product.stock}</p>
            <button onClick={() => addToCart(product)}>Add to Cart</button>
          </li>
        ))}
      </ul>
    </div>
  );
};

export default ProductList;
